mapper.select:
select
id, amount, description, consumption_date, create_time, create_by, update_time, update_by,
deleted
from book_keeping.expenses_record


    and id = #{id}


    and amount = #{amount}


    and description = #{description}


    and consumption_date = #{consumptionDate}


    and create_time = #{createTime}


    and create_by = #{createBy}


    and update_time = #{updateTime}


    and update_by = #{updateBy}


    and deleted = #{deleted}
